// src/components/LineChart.js
import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import {
  Chart as ChartJS,
  LineElement,
  PointElement,
  LinearScale,
  CategoryScale,
  Tooltip,
  Legend
} from "chart.js";
import "../styles/Chart.css";

ChartJS.register(LineElement, PointElement, LinearScale, CategoryScale, Tooltip, Legend);

function LineChart({ data }) {
  const products = [...new Set(data.map(d => d["Products and product groups"]))];
  const geos = [...new Set(data.map(d => d.GEO))];
  const uoms = [...new Set(data.map(d => d.UOM))];

  const [product, setProduct] = useState(products[0]);
  const [geo, setGeo] = useState(geos[0]);
  const [uom, setUom] = useState(uoms[0]);

  const filtered = data.filter(d =>
    d["Products and product groups"] === product &&
    d.GEO === geo &&
    d.UOM === uom
  );

  const grouped = {};
  filtered.forEach(d => {
    const date = d.REF_DATE;
    const value = parseFloat(d.VALUE);
    if (!grouped[date]) grouped[date] = 0;
    grouped[date] += value;
  });

  const sorted = Object.entries(grouped)
    .sort(([a], [b]) => a.localeCompare(b))
    .map(([date, val]) => ({ date, val }));

  const chartData = {
    labels: sorted.map(d => d.date),
    datasets: [
      {
        label: `${product} in ${geo} (${uom})`,
        data: sorted.map(d => d.val),
        fill: false,
        borderColor: "#3b82f6",
        tension: 0.4,
        pointRadius: 4,
        pointBackgroundColor: "#2563eb"
      }
    ]
  };

  return (
    <div className="line-chart-container">
      <div className="filters">
        <div className="filter-group">
          <label>Product</label>
          <select value={product} onChange={e => setProduct(e.target.value)}>
            {products.map(p => <option key={p}>{p}</option>)}
          </select>
        </div>
        <div className="filter-group">
          <label>Region</label>
          <select value={geo} onChange={e => setGeo(e.target.value)}>
            {geos.map(g => <option key={g}>{g}</option>)}
          </select>
        </div>
        <div className="filter-group">
          <label>Unit</label>
          <select value={uom} onChange={e => setUom(e.target.value)}>
            {uoms.map(u => <option key={u}>{u}</option>)}
          </select>
        </div>
      </div>
      <Line data={chartData} />
    </div>
  );
}

export default LineChart;
