import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import "../LineChart.css";

function LineChart({ data }) {
  const productOptions = [...new Set(data.map(d => d["Products and product groups"]))];
  const dateOptions = [...new Set(data.map(d => d.REF_DATE))];
  const geoOptions = [...new Set(data.map(d => d.GEO))];
  const uomOptions = [...new Set(data.map(d => d.UOM))];

  const [selectedProduct, setSelectedProduct] = useState(productOptions[0]);
  const [selectedDate, setSelectedDate] = useState("");
  const [selectedGeo, setSelectedGeo] = useState(geoOptions[0]);
  const [selectedUOM, setSelectedUOM] = useState(uomOptions[0]);

  const filtered = data.filter(d =>
    d["Products and product groups"] === selectedProduct &&
    (!selectedDate || d.REF_DATE === selectedDate) &&
    d.GEO === selectedGeo &&
    d.UOM === selectedUOM
  );

  const chartData = {
    labels: filtered.map(d => d.REF_DATE),
    datasets: [{
      label: selectedProduct,
      data: filtered.map(d => d.VALUE),
      borderColor: "#3b82f6",
      fill: false,
      tension: 0.3,
    }]
  };

  return (
    <div className="chart-box">
      <div className="filters">
        <div className="filter-group">
          <label>Product:</label>
          <select value={selectedProduct} onChange={e => setSelectedProduct(e.target.value)}>
            {productOptions.map(p => <option key={p}>{p}</option>)}
          </select>
        </div>

        <div className="filter-group">
          <label>Month:</label>
          <select value={selectedDate} onChange={e => setSelectedDate(e.target.value)}>
            <option value="">All</option>
            {dateOptions.map(d => <option key={d}>{d}</option>)}
          </select>
        </div>

        <div className="filter-group">
          <label>Region (Geo):</label>
          <select value={selectedGeo} onChange={e => setSelectedGeo(e.target.value)}>
            {geoOptions.map(g => <option key={g}>{g}</option>)}
          </select>
        </div>

        <div className="filter-group">
          <label>Unit:</label>
          <select value={selectedUOM} onChange={e => setSelectedUOM(e.target.value)}>
            {uomOptions.map(u => <option key={u}>{u}</option>)}
          </select>
        </div>
      </div>

      <Line data={chartData} />
    </div>
  );
}

export default LineChart;
