import React, { useState } from "react";
import Papa from "papaparse";
import LineChart from "./components/LineChart";
import BarChart from "./components/BarChart";
import "./DashboardTabs.css";

function App() {
  const [data, setData] = useState([]);
  const [lang, setLang] = useState("en");
  const [activeTab, setActiveTab] = useState("line");

  const translations = {
    en: {
      title: "University Enrolment Dashboard",
      lineChart: "Enrolment Over Time",
      barChart: "Gender Comparison",
      selectUni: "Select University",
      selectYear: "Select Year",
    },
    fr: {
      title: "Tableau de bord des inscriptions universitaires",
      lineChart: "Évolution des inscriptions",
      barChart: "Comparaison par sexe",
      selectUni: "Sélectionner l’université",
      selectYear: "Sélectionner l’année",
    },
  };

  React.useEffect(() => {
    Papa.parse("/enrolment_data.csv", {
      header: true,
      download: true,
      complete: (results) => {
        setData(results.data);
      },
    });
  }, []);

  return (
    <div className="dashboard-container">
      <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
        <h1>{translations[lang].title}</h1>
        <button onClick={() => setLang(lang === "en" ? "fr" : "en")}>
          {lang === "en" ? "FR" : "EN"}
        </button>
      </div>

      <div className="tab-content">
        {activeTab === "line" ? (
          <LineChart data={data} lang={lang} translations={translations[lang]} />
        ) : (
          <BarChart data={data} lang={lang} translations={translations[lang]} />
        )}
      </div>

      <div className="tab-bar">
        <button
          className={`tab-button ${activeTab === "line" ? "active" : ""}`}
          onClick={() => setActiveTab("line")}
        >
          {translations[lang].lineChart}
        </button>
        <button
          className={`tab-button ${activeTab === "bar" ? "active" : ""}`}
          onClick={() => setActiveTab("bar")}
        >
          {translations[lang].barChart}
        </button>
      </div>
    </div>
  );
}

export default App;
