// src/components/DonutChart.js
import React, { useState } from "react";
import { Doughnut } from "react-chartjs-2";
import {
  Chart as ChartJS,
  ArcElement,
  Tooltip,
  Legend
} from "chart.js";
import "../Chart.css";

ChartJS.register(ArcElement, Tooltip, Legend);

function DonutChart({ data }) {
  const products = [...new Set(data.map(d => d["Products and product groups"]))];
  const months = [...new Set(data.map(d => d.REF_DATE))];

  const [product, setProduct] = useState(products[0]);
  const [month, setMonth] = useState(months[0]);

  const filtered = data.filter(d =>
    d["Products and product groups"] === product &&
    d.REF_DATE === month
  );

  const grouped = {};
  filtered.forEach(d => {
    const geo = d.GEO;
    const value = parseFloat(d.VALUE);
    if (!grouped[geo]) grouped[geo] = 0;
    grouped[geo] += value;
  });

  const chartData = {
    labels: Object.keys(grouped),
    datasets: [
      {
        label: `${product} - ${month}`,
        data: Object.values(grouped),
        backgroundColor: [
          "#3b82f6", "#10b981", "#f59e0b",
          "#ef4444", "#6366f1", "#ec4899", "#14b8a6"
        ],
        borderWidth: 1
      }
    ]
  };

  // ðŸ‘‡ Options to control scaling
  const options = {
    responsive: true,
    maintainAspectRatio: false
  };

  return (
    <div className="donut-chart-container">
      <div className="filters">
        <div className="filter-group">
          <label>Product</label>
          <select value={product} onChange={e => setProduct(e.target.value)}>
            {products.map(p => <option key={p}>{p}</option>)}
          </select>
        </div>
        <div className="filter-group">
          <label>Month</label>
          <select value={month} onChange={e => setMonth(e.target.value)}>
            {months.map(m => <option key={m}>{m}</option>)}
          </select>
        </div>
      </div>

      {/* ðŸ‘‡ Resized Donut */}
      <div style={{ width: "400px", height: "400px", margin: "0 auto" }}>
        <Doughnut data={chartData} options={options} />
      </div>
    </div>
  );
}

export default DonutChart;
