import React, { useState } from "react";
import { Doughnut } from "react-chartjs-2";
import "./DonutChart.css";

function DonutChart({ data }) {
  const monthOptions = [...new Set(data.map(d => d.REF_DATE))];
  const [selectedMonth, setSelectedMonth] = useState(monthOptions[0]);

  const filtered = data.filter(d => d.REF_DATE === selectedMonth);

  const chartData = {
    labels: filtered.map(d => d["Products and product groups"]),
    datasets: [{
      data: filtered.map(d => d.VALUE),
      backgroundColor: [
        "#3b82f6", "#10b981", "#f59e0b", "#ef4444", "#6366f1",
        "#ec4899", "#14b8a6", "#8b5cf6", "#f43f5e", "#22d3ee"
      ]
    }]
  };

  return (
    <div className="chart-box">
      <div className="filters">
        <label>Month:</label>
        <select value={selectedMonth} onChange={e => setSelectedMonth(e.target.value)}>
          {monthOptions.map(m => <option key={m}>{m}</option>)}
        </select>
      </div>
      <Doughnut data={chartData} />
    </div>
  );
}

export default DonutChart;
