import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import LineChart from "./Components/LineChart";
import DoughnutChart from "./Components/DoughnutChart";
import "./DashboardTabs.css";

import {
  Chart as ChartJS,
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Title,
  Tooltip,
  Legend
);

function App() {
  const [data, setData] = useState([]);
  const [lang, setLang] = useState("en");

  const translations = {
    en: {
      title: "University Enrolment Dashboard",
      lineChart: "Enrolment Over Time",
      doughnutChart: "Student Status Breakdown",
      selectUni: "Select University",
      selectYear: "Select Year",
      selectField: "Select Field of Study",
      selectStatus: "Select Registration Status",
    },
    fr: {
      title: "Tableau de bord des inscriptions universitaires",
      lineChart: "Évolution des inscriptions",
      doughnutChart: "Répartition du statut de l'étudiant",
      selectUni: "Sélectionner l’université",
      selectYear: "Sélectionner l’année",
      selectField: "Sélectionner le champ d’étude",
      selectStatus: "Sélectionnez le statut d'inscription",
    },
  };

  useEffect(() => {
    const file = lang === "fr" ? "/FR.csv" : "/EN.csv";
    Papa.parse(file, {
      header: true,
      download: true,
      complete: (results) => {
        const validData = results.data.filter(row => row.VALUE && !isNaN(parseInt(row.VALUE)));
        setData(validData);
      },
    });
  }, [lang]);

  return (
    <div className="full-page">
      <div className="dashboard-container">
        <div className="dashboard-header">
          <h1>{translations[lang].title}</h1>
          <div className="lang-toggle">
            <button
              className={`lang-button ${lang === 'en' ? 'active' : ''}`}
              onClick={() => setLang('en')}
            >EN</button>
            <button
              className={`lang-button ${lang === 'fr' ? 'active' : ''}`}
              onClick={() => setLang('fr')}
            >FR</button>
          </div>
        </div>

        <div className="chart-row">
          <div className="chart-column">
            <LineChart data={data} lang={lang} translations={translations[lang]} />
          </div>
          <div className="chart-column">
            <DoughnutChart data={data} lang={lang} translations={translations[lang]} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
