import React, { useState } from "react";
import { Bar } from "react-chartjs-2";

function BarChart({ data, lang, translations }) {
  const years = [...new Set(data.map(row => row.REF_DATE))];
  const universities = [...new Set(data.map(row => row.GEO))];
  const [selectedYear, setSelectedYear] = useState(years[0]);
  const [selectedUni, setSelectedUni] = useState(universities[0]);

  const filtered = data.filter(row =>
    row.REF_DATE === selectedYear &&
    row.GEO === selectedUni &&
    row.Gender !== "Total gender"
  );

  const chartData = {
    labels: filtered.map(row => lang === "en" ? row.Gender : (row.Gender === "Male" ? "Homme" : "Femme")),
    datasets: [{
      label: translations.barChart,
      data: filtered.map(row => parseInt(row.VALUE)),
      backgroundColor: ["#3498db", "#e74c3c"]
    }]
  };

  return (
    <div>
      <h2>{translations.barChart}</h2>
      <label>{translations.selectYear}</label>
      <select value={selectedYear} onChange={e => setSelectedYear(e.target.value)}>
        {years.map(y => <option key={y}>{y}</option>)}
      </select>

      <label style={{ marginLeft: 10 }}>{translations.selectUni}</label>
      <select value={selectedUni} onChange={e => setSelectedUni(e.target.value)}>
        {universities.map(u => <option key={u}>{u}</option>)}
      </select>

      <Bar data={chartData} />
    </div>
  );
}

export default BarChart;
