import React from "react";
import data from "./Data"; // your large JSON
import LineChart from "./Components/LineChart";
import DonutChart from "./Components/DonutChart";
import "./Dashboard.css";
import {
  Chart as ChartJS,
  CategoryScale,   // âœ… this fixes "category" scale error
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend,
} from 'chart.js';

ChartJS.register(
  CategoryScale,
  LinearScale,
  PointElement,
  LineElement,
  ArcElement,
  Tooltip,
  Legend
);

function App() {

  const options = {
  responsive: true,
  maintainAspectRatio: false,
};
  return (


    <div className="dashboard-wrapper">
      <h1 className="dashboard-title">ðŸ“Š Food Prices Dashboard</h1>
      <div className="chart-row">
        <div className="chart-box">
          <h2 className="chart-title">Product Trend Over Time</h2>
          <LineChart data={data} />
        </div>
        <div className="chart-box">
          <h2 className="chart-title">Regional Breakdown</h2>
          <div className="donut-wrapper">
  <DonutChart data={data} options={options} />
</div>

        </div>
      </div>
    </div>



  );
}

export default App;
