import React, { useState } from "react";
import { Line } from "react-chartjs-2";
import '../LineChart.css';


function LineChart({ data, lang, translations }) {
    const universities = [...new Set(
        data.map(row => row.GEO).filter(s => s && s.trim() !== "")
    )];
    const fields = [...new Set(
        data.map(row => row["Field of study"]).filter(s => s && s.trim() !== "")
    )];
    const statuses = [...new Set(
        data.map(row => row["Registration status"]).filter(s => s && s.trim() !== "")
    )];
    const genders = [...new Set(
        data.map(row => row.Gender).filter(s => s && s.trim() !== "")
    )];

    const [selectedUni, setSelectedUni] = useState(universities[0]);
    const [selectedField, setSelectedField] = useState(fields[0]);
    const [selectedStatus, setSelectedStatus] = useState(statuses[0]);
    const [selectedGender, setSelectedGender] = useState(genders[0]);

    const filtered = data.filter(row =>
        row.GEO === selectedUni &&
        row["Field of study"] === selectedField &&
        row["Registration status"] === selectedStatus &&
        row.Gender === selectedGender &&
        row.VALUE &&
        row.REF_DATE
    );

    const sorted = [...filtered].sort((a, b) => a.REF_DATE.localeCompare(b.REF_DATE));

   const grouped = {};
filtered.forEach(row => {
  const year = row.REF_DATE;
  const value = parseInt(row.VALUE) || 0;
  if (grouped[year]) {
    grouped[year] += value;
  } else {
    grouped[year] = value;
  }
});

// Sort by year and format for chart
const sortedYears = Object.keys(grouped).sort();
const chartData = {
  labels: sortedYears,
  datasets: [{
    label: `${translations.lineChart} - ${selectedField} - ${selectedStatus} - ${selectedGender}`,
    data: sortedYears.map(year => grouped[year]),
    fill: false,
    borderColor: "#2563eb",
    tension: 0.3,
    pointRadius: 4,
    pointHoverRadius: 6
  }]
};

    return (
        <div className="line-chart-container">
            <h2>{translations.lineChart}</h2>

            <div className="line-chart-filters">
                <div className="dropdown-block">
                    <label>{translations.selectUni}</label>
                    <select value={selectedUni} onChange={e => setSelectedUni(e.target.value)}>
                        {universities.map(u => <option key={u}>{u}</option>)}
                    </select>
                </div>

                <div className="dropdown-block">
                    <label>{translations.selectField}</label>
                    <select value={selectedField} onChange={e => setSelectedField(e.target.value)}>
                        {fields.map(f => <option key={f}>{f}</option>)}
                    </select>
                </div>

                <div className="dropdown-block">
                    <label>{translations.selectStatus}</label>
                    <select value={selectedStatus} onChange={e => setSelectedStatus(e.target.value)}>
                        {statuses.map(s => <option key={s}>{s}</option>)}
                    </select>
                </div>

                <div className="dropdown-block">
                    <label>Gender</label>
                    <select value={selectedGender} onChange={e => setSelectedGender(e.target.value)}>
                        {genders.map(g => <option key={g}>{g}</option>)}
                    </select>
                </div>
            </div>

            <Line data={chartData} />
        </div>
    );
}

export default LineChart;
