import React, { useState } from "react";
import { Doughnut } from "react-chartjs-2";
import "../DoughnutChart.css";

function DoughnutChart({ data, translations }) {
  const fields = [...new Set(data.map(row => row["Field of study"]).filter(Boolean))];
  const years = [...new Set(data.map(row => row["REF_DATE"]).filter(Boolean))];
  const institutions = [...new Set(data.map(row => row["Institution type"]).filter(Boolean))];

  const [selectedField, setSelectedField] = useState(fields[0]);
  const [selectedYear, setSelectedYear] = useState(years[0]);
  const [selectedInstitution, setSelectedInstitution] = useState(institutions[0]);

  const filtered = data.filter(row =>
    row["Field of study"] === selectedField &&
    row["REF_DATE"] === selectedYear &&
    row["Institution type"] === selectedInstitution &&
    row.VALUE &&
    row["Status of student in Canada"] &&
    row["Gender"]
  );

  const aggregated = {};
  filtered.forEach(row => {
    const status = row["Status of student in Canada"];
    const gender = row["Gender"];
    const key = `${status} - ${gender}`;
    const value = parseInt(row.VALUE) || 0;

    if (!aggregated[key]) aggregated[key] = 0;
    aggregated[key] += value;
  });

  const labels = Object.keys(aggregated);
  const values = labels.map(label => aggregated[label]);

  const chartData = {
    labels,
    datasets: [{
      data: values,
      backgroundColor: [
        "#3b82f6", "#60a5fa", "#10b981", "#34d399",
        "#f59e0b", "#fbbf24", "#ef4444", "#f87171",
        "#6366f1", "#8b5cf6"
      ]
    }]
  };

  const chartOptions = {
    maintainAspectRatio: false, // Key to make it flexible in fixed size container
    plugins: {
      tooltip: {
        callbacks: {
          label: function (context) {
            const value = context.raw;
            const total = context.chart._metasets[0].total;
            const percent = ((value / total) * 100).toFixed(1);
            return `${context.label}: ${value} (${percent}%)`;
          }
        }
      },
      legend: {
        position: 'bottom'
      }
    }
  };

  return (
    <div className="doughnut-chart-container">
      <h2>{translations.doughnutChartTitle}</h2>

      <div className="doughnut-chart-filters">
        <div className="dropdown-block">
          <label>{translations.selectField}</label>
          <select value={selectedField} onChange={e => setSelectedField(e.target.value)}>
            {fields.map(f => <option key={f}>{f}</option>)}
          </select>
        </div>
        <div className="dropdown-block">
          <label>{translations.selectYear}</label>
          <select value={selectedYear} onChange={e => setSelectedYear(e.target.value)}>
            {years.map(y => <option key={y}>{y}</option>)}
          </select>
        </div>
        <div className="dropdown-block">
          <label>{translations.selectInstitution}</label>
          <select value={selectedInstitution} onChange={e => setSelectedInstitution(e.target.value)}>
            {institutions.map(i => <option key={i}>{i}</option>)}
          </select>
        </div>
      </div>

      {/* 🔧 Responsive wrapper for size control */}
      <div style={{ width: '100%', maxWidth: '600px', height: '400px', margin: '0 auto' }}>
        <Doughnut data={chartData} options={chartOptions} />
      </div>
    </div>
  );
}

export default DoughnutChart;
