import React from "react";
import { Line } from "react-chartjs-2";

function LineChart({ data }) {
  const grouped = {};
  data.forEach(row => {
    if (!grouped[row["Products and product groups"]]) {
      grouped[row["Products and product groups"]] = [];
    }
    grouped[row["Products and product groups"]].push({ x: row["REF_DATE"], y: row.VALUE });
  });

  const chartData = {
    datasets: Object.entries(grouped).map(([label, values], index) => ({
      label,
      data: values,
      borderColor: ["#3b82f6", "#ec4899", "#f59e0b"][index],
      fill: false,
      tension: 0.3
    }))
  };

  const options = {
    responsive: true,
    plugins: {
      legend: { position: "top" },
      title: { display: true, text: "Price Trend Over Time" }
    },
    scales: {
      x: { title: { display: true, text: "Month" } },
      y: { title: { display: true, text: "Index Value" } }
    }
  };

  return <Line data={chartData} options={options} />;
}

export default LineChart;
