import React, { useState, useEffect } from "react";
import Papa from "papaparse";
import LineChart from "./Components/LineChart";
import BarChart from "./Components/BarChart";
import "./DashboardTabs.css";

function App() {
  const [dataEn, setDataEn] = useState([]);
  const [dataFr, setDataFr] = useState([]);
  const [lang, setLang] = useState("en");

  const translations = {
    en: {
      title: "University Enrolment Dashboard",
      lineChart: "Enrolment Over Time",
      barChart: "Gender Distribution",
      selectUni: "Select University",
      selectYear: "Select Year",
      selectField: "Select Field of Study",
      selectStatus: "Select Registration Status",
    },
    fr: {
      title: "Tableau de bord des inscriptions universitaires",
      lineChart: "Évolution des inscriptions",
      barChart: "Répartition par sexe",
      selectUni: "Sélectionner l’université",
      selectYear: "Sélectionner l’année",
      selectField: "Sélectionner le champ d’étude",
      selectStatus: "Sélectionnez le statut d'inscription",
    },
  };

  useEffect(() => {
    Papa.parse("/enrolment_en.csv", {
      header: true,
      download: true,
      complete: (results) => setDataEn(results.data),
    });

    Papa.parse("/enrolment_fr.csv", {
      header: true,
      download: true,
      complete: (results) => setDataFr(results.data),
    });
  }, []);

  const data = lang === "fr" ? dataFr : dataEn;

  return (
    <div className="full-page">
      <div className="dashboard-container">
        <div className="dashboard-header">
          <h1>{translations[lang].title}</h1>
          <div className="lang-toggle">
            <button
              className={`lang-button ${lang === 'en' ? 'active' : ''}`}
              onClick={() => setLang('en')}
            >EN</button>
            <button
              className={`lang-button ${lang === 'fr' ? 'active' : ''}`}
              onClick={() => setLang('fr')}
            >FR</button>
          </div>
        </div>

        <div className="chart-row">
          <div className="chart-column">
            <LineChart data={data} lang={lang} translations={translations[lang]} />
          </div>
          <div className="chart-column">
            <BarChart data={data} lang={lang} translations={translations[lang]} />
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
