import React, { useState } from "react";
import { Doughnut } from "react-chartjs-2";
import "../DoughnutChart.css";

function DonutChart({ data }) {
  const products = [...new Set(data.map(d => d["Products and product groups"]))];
  const dates = [...new Set(data.map(d => d.REF_DATE))];
  const uoms = [...new Set(data.map(d => d.UOM))];

  const [product, setProduct] = useState(products[0]);
  const [date, setDate] = useState(dates[0]);
  const [uom, setUom] = useState(uoms[0]);

  const filtered = data.filter(d =>
    d["Products and product groups"] === product &&
    d.REF_DATE === date &&
    d.UOM === uom
  );

  const chartData = {
    labels: filtered.map(d => d.GEO),
    datasets: [{
      label: `${product} breakdown in ${date} (${uom})`,
      data: filtered.map(d => d.VALUE),
      backgroundColor: ["#3b82f6", "#10b981", "#f59e0b", "#ef4444"]
    }]
  };

  return (
    <div className="chart-box">
      <h2>Regional Breakdown (Donut)</h2>
      <div className="filters">
        <div className="filter-group">
          <label>Product</label>
          <select value={product} onChange={e => setProduct(e.target.value)}>
            {products.map(p => <option key={p}>{p}</option>)}
          </select>
        </div>
        <div className="filter-group">
          <label>Month</label>
          <select value={date} onChange={e => setDate(e.target.value)}>
            {dates.map(d => <option key={d}>{d}</option>)}
          </select>
        </div>
        <div className="filter-group">
          <label>Unit</label>
          <select value={uom} onChange={e => setUom(e.target.value)}>
            {uoms.map(u => <option key={u}>{u}</option>)}
          </select>
        </div>
      </div>
      <Doughnut data={chartData} />
    </div>
  );
}

export default DonutChart;
